<?php	include('..\..\..\GestionVoiture\Modele\Respostry\transactionVoiture.php');	class controllerVoiture	{		public $tabVoiture;		public $nombre;		public $tousVoiture;		public function controllerVoiture(){}				public function addVoiture($numero,$type,$marque,$idPers)		{			$voitures=new transactionVoiture();			$voitures->setNumero($numero)->setType($type)->setMarque($marque)->setIdPers($idPers)->insertion();		}		/*		public function deletePersonne($id)		{			$personnes=new transactionPersonne();			$personnes->setId($id);			$personnes->supprimer();		}				public function updatePersonne($id,$nom,$prenom,$adresse)		{			$personnes=new transactionPersonne();			$personnes->setId($id);			$personnes->setNom($nom);			$personnes->setPrenom($prenom);			$personnes->setAdresse($adresse);			$personnes->modifier();		}		*/		public function selectVoiture($idPers)		{						$this->tabVoiture=array();			$voiture=new transactionVoiture();			$this->tabVoiture=$voiture->afficherParPersonne($idPers);			$nombre=$voiture->count($idPers); 			$this->nombre=$nombre;		}		public function allVoiture()		{			$voiture=new transactionVoiture();			$this->tousVoiture=$voiture->affichetous();			$this->nombre=$voiture->nombreVoiture;		}		public function transfert($id,$idPers)		{			$voiture=new transactionVoiture();			$voiture->setId($id);			$voiture->setIdPers($idPers);			$voiture->transferer();								}	}?>